Wormies.Views.StageView=Backbone.View.extend({initialize:function(){return this.collection.bind("add",$.proxy(this.onParticipantAdd,this)),this.collection.bind("remove",$.proxy(this.onParticipantRemove,this))},onParticipantAdd:function(a){var b,c;return b=$(this.make("div",{id:a.get("id"),"class":a.get("state")})),b.appendTo(this.el),c=new Wormies.Views.ParticipantView({el:b,model:a}),c.render()},onParticipantRemove:function(a){return $("#"+a.get("id")).remove()}})